SELECT #{tableNm} CODE_ID, ORGNZT_ID CODE, ORGNZT_NM CODE_NM, ORGNZT_DC CODE_DC FROM LETTNORGNZTINFO WHERE 1=1
AND ORGNZT_ID LIKE #{detailConditionValue}
ORDER BY ORGNZT_NM

SELECT #{tableNm} CODE_ID, ORGNZT_ID CODE, ORGNZT_NM CODE_NM, ORGNZT_DC CODE_DC FROM LETTNORGNZTINFO WHERE 1=1 AND ORGNZT_UP IS NULL OR ORGNZT_UP = 'ITEYES'
AND ORGNZT_ID LIKE #{detailConditionValue}
