mapper.select:
SELECT uniqId, userTy, userId , userNm, moblphonNo, orgnztId, empUniqNum, sttus, sbscrbDe, grade, authorCode FROM (SELECT A.ESNTL_ID uniqId, 'USR03' userTy, A.EMPLYR_ID userId, A.USER_NM userNm, A.MBTLNUM moblphonNo, B.ORGNZT_NM orgnztId, A.EMP_UNIQ_NUM empUniqNum, A.EMPLYR_STTUS_CODE sttus, DATE_FORMAT(A.SBSCRB_DE, '%Y %m %d') as sbscrbDe, C.CODE_NM grade, D.AUTHOR_NM authorCode, A.EM_ORDER emOrder FROM LETTNEMPLYRINFO A LEFT OUTER JOIN LETTNORGNZTINFO B ON B.ORGNZT_ID = A.ORGNZT_ID LEFT OUTER JOIN LETTCCMMNDETAILCODE C ON C.CODE = A.GRADE LEFT OUTER JOIN LETTNEMPLYRSCRTYESTBS E ON E.SCRTY_DTRMN_TRGET_ID = A.ESNTL_ID LEFT OUTER JOIN LETTNAUTHORINFO D ON D.AUTHOR_CODE = E.AUTHOR_CODE WHERE E.AUTHOR_CODE != 'ROLE_HIGH_ADMIN' AND A.EMPLYR_ID != 'empty@iteyes.co.kr' AND A.EMPLYR_STTUS_CODE = 'P'
AND D.AUTHOR_CODE = #{searchAuthor}
AND (A.EMPLYR_ID LIKE '%' #{searchKeyword} '%' OR A.USER_NM LIKE '%' #{searchKeyword} '%' OR A.EMP_UNIQ_NUM LIKE '%' #{searchKeyword} '%')
AND (B.ORGNZT_ID = #{searchOrgnzt} OR B.ORGNZT_UP = #{searchOrgnzt})
AND B.ORGNZT_ID = #{searchLOrgnzt}
AND C.CODE = #{searchGrade}
) A ORDER BY emOrder ASC, userNm ASC, sbscrbDe DESC LIMIT #{recordCountPerPage} OFFSET #{firstIndex}

SELECT uniqId, userTy, userId , userNm, moblphonNo, orgnztId, sttus, sbscrbDe, grade, authorCode FROM (SELECT A.ESNTL_ID uniqId, 'USR03' userTy, A.EMPLYR_ID userId, A.USER_NM userNm, A.MBTLNUM moblphonNo, B.ORGNZT_NM orgnztId, A.EMPLYR_STTUS_CODE sttus, DATE_FORMAT(A.SBSCRB_DE, '%Y %m %d') as sbscrbDe, C.CODE_NM grade, D.AUTHOR_NM authorCode, A.EM_ORDER emOrder FROM LETTNEMPLYRINFO A LEFT OUTER JOIN LETTNORGNZTINFO B ON B.ORGNZT_ID = A.ORGNZT_ID LEFT OUTER JOIN LETTCCMMNDETAILCODE C ON C.CODE = A.GRADE LEFT OUTER JOIN LETTNEMPLYRSCRTYESTBS E ON E.SCRTY_DTRMN_TRGET_ID = A.ESNTL_ID LEFT OUTER JOIN LETTNAUTHORINFO D ON D.AUTHOR_CODE = E.AUTHOR_CODE WHERE A.EMPLYR_ID != 'admin' AND A.EMPLYR_STTUS_CODE = 'P'
AND D.AUTHOR_CODE = #{searchAuthor}
AND (A.EMPLYR_ID LIKE '%' #{searchKeyword} '%' OR A.USER_NM LIKE '%' #{searchKeyword} '%' OR A.EMP_UNIQ_NUM LIKE '%' #{searchKeyword} '%')
AND (B.ORGNZT_ID = #{searchOrgnzt} OR B.ORGNZT_UP = #{searchOrgnzt})
AND B.ORGNZT_ID = #{searchLOrgnzt}
AND C.CODE = #{searchGrade}
AND A.EMPLYR_ID != 'empty@iteyes.co.kr'
) A ORDER BY emOrder ASC, userNm ASC, sbscrbDe DESC LIMIT #{recordCountPerPage} OFFSET #{firstIndex}

